/* eslint-disable no-console */
<template>
  <div>
    <div class="container">
      <router-view />
      <Map class="map" />
      <div class="ui two bottom attached buttons">
        <button class="ui button" v-on:click="prev()">Prev</button>
        <button class="ui button" v-on:click="next()">Next</button>
      </div>
    </div>
  </div>
</template>
<script>
import Map from "@/components/Map.vue";
import store from "../store/index.js";
import { mapState } from "vuex";

export default {
  name: "Recherche",

  components: {
    Map
  },
  created: function() {
    store.dispatch("appel", {
      page: 1
    });
    store.dispatch("allGare");
  },
  computed: mapState(["result", "nbpage", "allgare", "nameGare"]),
  methods: {
    clique: function() {
      this.listeGare = store.state.result;
    },
    clickCallback: function(pageNum) {
      store.dispatch("turnPage", {
        page: pageNum
      });
    },

    prev: function() {
      if (store.state.etatPage == 0) {
        var pageP = store.state.numpage - 1;
        if (pageP < 1) {
          pageP = Math.round(store.state.queryList.length / 15);
        }
        store.dispatch("turnPage", {
          page: pageP
        });
      } else {
        var indexP = store.state.indexPage - 1;
        if (indexP < 0) {
          indexP = store.state.queryList.length - 1;
        }
        store.dispatch("changeCurrentGare", {
          index: indexP
        });
      }
    },
    next: function() {
      if (store.state.etatPage == 0) {
        // eslint-disable-next-line no-console
        console.log(Math.round(store.state.queryList.length / 15));
        var pageP = store.state.numpage + 1;
        if (pageP > Math.round(store.state.queryList.length / 15)) {
          pageP = 1;
        }
        store.dispatch("turnPage", {
          page: pageP
        });
      } else {
        // eslint-disable-next-line no-console
        console.log(store.state.indexPage);
        var indexP = store.state.indexPage + 1;
        // eslint-disable-next-line no-console
        console.log(indexP);
        if (indexP > store.state.queryList.length - 1) {
          indexP = 0;
        }
        // eslint-disable-next-line no-console
        console.log(store.state.queryList.length);
        // eslint-disable-next-line no-console
        console.log(indexP);
        store.dispatch("changeCurrentGare", {
          index: indexP
        });
      }
    }
  }
};
</script>
<style type="text/css">
.container {
  display: grid;
  grid-template-columns: 7fr 4fr;
  grid-column-gap: 20px;
}
.list {
  height: 90vh;
}
.map {
  position: sticky;
  top: 0;
  left: 0;
  right: 0;
  height: 90vh;
}
.element {
  display: grid;
}
.pagination {
  display: grid;
  list-style: none;
  grid-template-columns: repeat(10, 1fr);
}
</style>
